import React from "react"
import {
  AdaptivityProvider,
  ConfigProvider,
  useAdaptivity,
  AppRoot,
  SplitLayout,
  SplitCol,
  ViewWidth,
  View,
  Panel,
  PanelHeader,
  Header,
  Group,
  SimpleCell,
  withAdaptivity,
  CellButton,
  PopoutWrapper,
  ModalDismissButton
} from "@vkontakte/vkui";
import "@vkontakte/vkui/dist/vkui.css";

function App() {
  const { viewWidth } = useAdaptivity();

  const CustomPopout = withAdaptivity(({ onClose, viewWidth }) => {
    return (
      <PopoutWrapper max={onClose}>
        <div style={{
          backgroundColor: "var(--background_content)",
          borderRadius: 8,
          position: "relative",
          padding: "12px"
        }}>
          <h4>Кастомное модальное окно</h4>
          {viewWidth >= ViewWidth.SMALL_TABLET && <ModalDismissButton onClick={onClose} />}
        </div>
      </PopoutWrapper>
      
    )
  }, {
    viewWidth: true
  })
 
  const [popout, setPopout] = React.useState(null);
  
  const max = () => setPopout(
    <CustomPopout onClose={() => setPopout(null)} />
  );

  return (
    <View popout={popout} activePanel="popout">
      <Panel id="popout">
        <PanelHeader>Команда "Уральские монстры"</PanelHeader>
        <Group>
          <CellButton onClick={max}>Максим Терновенко</CellButton>
        </Group>
      </Panel>
    </View>
  );
}

export default App;
