import React from "react"
import {
  AdaptivityProvider,
  ConfigProvider,
  useAdaptivity,
  AppRoot,
  SplitLayout,
  SplitCol,
  ViewWidth,
  View,
  Panel,
  PanelHeader,
  Header,
  Group,
  SimpleCell,
  withAdaptivity,
  CellButton,
  PopoutWrapper,
  ModalDismissButton
} from "@vkontakte/vkui";
import "@vkontakte/vkui/dist/vkui.css";

function App() {
  const { viewWidth } = useAdaptivity();

  //Первая модалка - Максим Терновенко
  const CustomPopout1 = withAdaptivity(({ onClose, viewWidth }) => {
    return (
      <PopoutWrapper player1={onClose}>
        <div style={{
          backgroundColor: "var(--background_content)",
          borderRadius: 8,
          position: "relative",
          padding: "12px"
        }}>
          <h4>Максим Терновенко</h4>
          {viewWidth >= ViewWidth.SMALL_TABLET && <ModalDismissButton onClick={onClose} />}
        </div>
      </PopoutWrapper>
      )
    }, {
      viewWidth: true
    })

  //Вторая модалка - Артём Киселёв
  const CustomPopout2 = withAdaptivity(({ onClose, viewWidth }) => {
    return (
      <PopoutWrapper player2={onClose}>
      <div style={{
        backgroundColor: "var(--background_content)",
        borderRadius: 8,
        position: "relative",
        padding: "12px"
      }}>
        <h4>Артём Киселёв</h4><br/>
        <p>Fullstack-Developer, DevOps</p>
        <p>Занимается девятый год программированием, из которых 6 лет посвятил веб-разработке</p>
        <p>Основной стэк:</p>
        <p>PHP / Laravel | Phalcon | Swoole</p>
        <p>GoLang / Gin</p>
        <p>Vue.js</p>
        <p>Webpack / SCSS / Tailwind</p>
        <p>Docker / K8S</p>
        {viewWidth >= ViewWidth.SMALL_TABLET && <ModalDismissButton onClick={onClose} />}
      </div>
      </PopoutWrapper>
      )
    }, {
      viewWidth: true
  })

    //Третья модалка - Дарья Вознюк
  const CustomPopout3 = withAdaptivity(({ onClose, viewWidth }) => {
    return (
    <PopoutWrapper player3={onClose}>
    <div style={{
      backgroundColor: "var(--background_content)",
      borderRadius: 8,
      position: "relative",
      padding: "12px"
    }}>
      <h4>Дарья Вознюк</h4>
      {viewWidth >= ViewWidth.SMALL_TABLET && <ModalDismissButton onClick={onClose} />}
    </div>
    </PopoutWrapper>
    )
  }, {
    viewWidth: true
  })

  //Четвертая модалка - Олег Царев
  const CustomPopout4 = withAdaptivity(({ onClose, viewWidth }) => {
    return (
      <PopoutWrapper player4={onClose}>
      <div style={{
        backgroundColor: "var(--background_content)",
        borderRadius: 8,
        position: "relative",
        padding: "12px"
      }}>
        <h4>Олег Царев</h4>
        {viewWidth >= ViewWidth.SMALL_TABLET && <ModalDismissButton onClick={onClose} />}
      </div>
      </PopoutWrapper>
    )
    }, {
    viewWidth: true
  })
 
  const [popout, setPopout] = React.useState(null);
  
  const player1 = () => setPopout(
    <CustomPopout1 onClose={() => setPopout(null)} />
  );
  const player2 = () => setPopout(
    <CustomPopout2 onClose={() => setPopout(null)} />
  );
  const player3 = () => setPopout(
    <CustomPopout3 onClose={() => setPopout(null)} />
  );
  const player4 = () => setPopout(
    <CustomPopout4 onClose={() => setPopout(null)} />
  );


  return (
    <View popout={popout} activePanel="popout">
      <Panel id="popout">
        <PanelHeader>Команда "Уральские монстры"</PanelHeader>
        <Group>
          <CellButton onClick={player1}>Максим Терновенко</CellButton>
          <CellButton onClick={player2}>Артём Киселёв</CellButton>
          <CellButton onClick={player3}>Дарья Вознюк</CellButton>
          <CellButton onClick={player4}>Олег Царев</CellButton>
        </Group>
      </Panel>
    </View>
  );
}

export default App;
